// Función helper para acceder a nodos de forma segura
function safeGetNodeData(nodeName) {
  try {
    const node = $(nodeName);
    return node?.item?.json || {};
  } catch (e) {
    return {};
  }
}

// Consolidar las 4 memorias avanzadas
const preferenceMemory = safeGetNodeData('HTTP Request - Load Preference Memory');
const performanceMemory = safeGetNodeData('HTTP Request - Load Performance Memory');
const constraintMemory = safeGetNodeData('HTTP Request - Load Constraint Memory');
const patternMemoryData = safeGetNodeData('HTTP Request - Load Pattern Memory');
const baseData = safeGetNodeData('Normalize Memory');

// Obtener lastCognitiveVersion de HTTP Request - Get Last Cognitive Version
const lastCognitiveVersionResponse = safeGetNodeData('HTTP Request - Get Last Cognitive Version');
const lastPack = Array.isArray(lastCognitiveVersionResponse) ? 
  (lastCognitiveVersionResponse[0] || {}) : 
  (lastCognitiveVersionResponse.data?.[0] || lastCognitiveVersionResponse);
const lastCognitiveVersion = lastPack?.cognitiveVersion ?? 
  (lastPack?.metadata ? (JSON.parse(lastPack.metadata)?.cognitiveVersion ?? null) : null) ?? 1;

// Extraer patrones fallidos y exitosos de PatternMemory
// Los patrones vienen como memorias individuales, necesitamos parsearlas
// Puede venir como array directo, en data, o en learningsList
const patternMemories = Array.isArray(patternMemoryData) ? patternMemoryData : 
  (patternMemoryData.data ? patternMemoryData.data : 
   (patternMemoryData.learningsList ? patternMemoryData.learningsList : []));
const failedPatterns = [];
const successfulPatterns = [];

// BLOQUEO TEMPORAL: Identificar patrones fallidos repetidamente
// FASE 3: Soporte para severity/blockStatus determinístico
const patternFailureCount = {};
const blockedPatterns = [];
const patternTimestamps = {};

// Función determinística para calcular días desde timestamp
function daysSinceTimestamp(timestamp, referenceTimestamp) {
  if (!timestamp || !referenceTimestamp) return 999; // Si no hay timestamp, asumir muy antiguo
  try {
    const ts = new Date(timestamp).getTime();
    const ref = new Date(referenceTimestamp).getTime();
    if (isNaN(ts) || isNaN(ref)) return 999;
    return Math.round(((ref - ts) / (1000 * 60 * 60 * 24)) * 10000) / 10000; // Redondear a 4 decimales
  } catch (e) {
    return 999;
  }
}

// Obtener timestamp de referencia determinístico (del validatedData si existe)
const validatedDataNode = safeGetNodeData('Set Validated Data');
const validatedData = validatedDataNode?.validatedData || {};
const referenceTimestamp = validatedData.receivedAt || new Date().toISOString();

patternMemories.forEach(memory => {
  try {
    const content = typeof memory.content === 'string' ? JSON.parse(memory.content) : memory.content;
    const pattern = content.pattern || JSON.stringify(content);

    if (content.result === 'negative' || (content.penalty && content.penalty < 0) || content.result === 'override_result') {
      // Contar fallos por patrón
      patternFailureCount[pattern] = (patternFailureCount[pattern] || 0) + 1;
      const memoryTimestamp = content.timestamp || content.context?.evaluationTime || referenceTimestamp;
      patternTimestamps[pattern] = memoryTimestamp;

      // Extraer severity si existe (de Fase 3)
      const severity = content.severity || 'moderate'; // Default si no existe
      const penalty = Math.round((content.penalty || -0.2) * 10000) / 10000; // Redondear a 4 decimales

      // Calcular días desde fallo (determinístico)
      const daysSinceFailure = daysSinceTimestamp(memoryTimestamp, referenceTimestamp);

      // Si un patrón falla 3+ veces, bloquearlo temporalmente (30 días)
      if (patternFailureCount[pattern] >= 3 && daysSinceFailure < 30) {
        blockedPatterns.push(pattern);
      }

      failedPatterns.push({ 
        pattern: pattern, 
        failureCount: patternFailureCount[pattern], 
        penalty: penalty,
        severity: severity,
        timestamp: memoryTimestamp,
        daysSinceFailure: daysSinceFailure
      });
    } else if (content.result === 'positive' || (content.penalty && content.penalty > 0)) {
      const penalty = Math.round((content.penalty || 0.1) * 10000) / 10000; // Redondear a 4 decimales
      successfulPatterns.push({ pattern: pattern, penalty: penalty });
    }
  } catch (e) {
    // Ignorar errores de parsing
  }
});

// Extraer datos estructurados de cada tipo de memoria
// Las APIs devuelven: { preferences: {...}, learnings: {...}, restrictions: [...] }
// baseData viene de HTTP Request - Load Marketing Memory que devuelve la misma estructura
const advancedMemory = {
  preferenceMemory: {
    preferredTone: preferenceMemory.preferences?.preferredTone || baseData.preferences?.preferredTone || 'profesional',
    preferredFormats: preferenceMemory.preferences?.preferredFormats || baseData.preferences?.preferredFormats || [],
    preferredChannels: preferenceMemory.preferences?.preferredChannels || baseData.preferences?.preferredChannels || [],
    dislikedFormats: preferenceMemory.preferences?.dislikedFormats || baseData.preferences?.dislikedFormats || [],
    stylePreferences: preferenceMemory.preferences?.stylePreferences || baseData.preferences?.stylePreferences || {}
  },
  performanceMemory: {
    bestPerformingChannels: performanceMemory.learnings?.bestPerformingChannels || baseData.learnings?.bestPerformingChannels || [],
    channelKPIs: performanceMemory.learnings?.channelKPIs || baseData.learnings?.channelKPIs || {},
    bestTimes: performanceMemory.learnings?.bestTimes || baseData.learnings?.bestTimes || [],
    bestDays: performanceMemory.learnings?.bestDays || baseData.learnings?.bestDays || [],
    avgCTR: performanceMemory.learnings?.avgCTR || baseData.learnings?.avgCTR || 0,
    avgEngagement: performanceMemory.learnings?.avgEngagement || baseData.learnings?.avgEngagement || 0,
    topPerformers: performanceMemory.learnings?.topPerformers || baseData.learnings?.topPerformers || []
  },
  constraintMemory: {
    restrictions: constraintMemory.restrictions || baseData.restrictions || [],
    prohibitedChannels: (constraintMemory.restrictions || baseData.restrictions || []).filter(r => typeof r === 'string' && (r.includes('channel') || r.includes('prohibited'))),
    legalConstraints: (constraintMemory.restrictions || baseData.restrictions || []).filter(r => typeof r === 'string' && (r.includes('legal') || r.includes('compliance'))),
    brandConstraints: (constraintMemory.restrictions || baseData.restrictions || []).filter(r => typeof r === 'string' && (r.includes('brand') || r.includes('guidelines')))
  },
  patternMemory: {
    successfulPatterns: successfulPatterns,
    failedPatterns: failedPatterns,
    blockedPatterns: blockedPatterns,
    patternFailureCount: patternFailureCount,
    patternTimestamps: patternTimestamps,
    urgencyFormatMapping: patternMemoryData.urgencyFormatMapping || {},
    channelFormatMapping: patternMemoryData.channelFormatMapping || {},
    toneChannelMapping: patternMemoryData.toneChannelMapping || {}
  }
};

// Calcular confidence weights basados en historial
const confidenceWeights = {
  channel: calculateChannelConfidence(advancedMemory.performanceMemory.bestPerformingChannels, advancedMemory.performanceMemory.channelKPIs),
  format: calculateFormatConfidence(advancedMemory.patternMemory.urgencyFormatMapping, advancedMemory.performanceMemory.topPerformers),
  tone: calculateToneConfidence(advancedMemory.performanceMemory.topPerformers, advancedMemory.patternMemory.toneChannelMapping),
  timing: calculateTimingConfidence(advancedMemory.performanceMemory.bestTimes, advancedMemory.performanceMemory.bestDays)
};

function calculateChannelConfidence(bestChannels, channelKPIs) {
  const weights = {};
  if (bestChannels && bestChannels.length > 0) {
    bestChannels.forEach((channel, index) => {
      const kpi = channelKPIs[channel] || {};
      const ctr = kpi.ctr || 0;
      const engagement = kpi.engagement || 0;

      // AUTO-AJUSTE: Penaliza canales con bajo rendimiento y refuerza exitosos
      let baseWeight = 0.5 + (ctr * 10) + (engagement * 5); // CTR y engagement influyen directamente

      // Penalización fuerte si CTR < 0.5% o engagement < 1%
      if (ctr < 0.005) baseWeight -= 0.3;
      if (engagement < 0.01) baseWeight -= 0.2;

      // Refuerzo si CTR > 2% o engagement > 5%
      if (ctr > 0.02) baseWeight += 0.2;
      if (engagement > 0.05) baseWeight += 0.15;

      // Ajuste por posición (los primeros tienen más peso)
      baseWeight -= (index * 0.05);

      weights[channel] = Math.max(0.1, Math.min(0.9, baseWeight));
    });
  }
  return weights;
}

function calculateFormatConfidence(urgencyFormatMapping, topPerformers) {
  const weights = {};

  // AUTO-AJUSTE: Refuerza formatos exitosos y penaliza fallidos
  if (urgencyFormatMapping && Object.keys(urgencyFormatMapping).length > 0) {
    Object.entries(urgencyFormatMapping).forEach(([urgency, format]) => {
      // Si está en el mapeo, es porque ha sido exitoso
      weights[`${urgency}_${format}`] = 0.75; // Aumentado de 0.7 a 0.75
    });
  }

  if (topPerformers && topPerformers.length > 0) {
    topPerformers.forEach((performer, index) => {
      if (performer.format) {
        // Los top performers refuerzan más
        const boost = 0.15 - (index * 0.02); // Más boost para los primeros
        weights[performer.format] = (weights[performer.format] || 0.5) + boost;
      }
    });
  }

  // Normalizar todos los pesos
  Object.keys(weights).forEach(key => {
    weights[key] = Math.max(0.1, Math.min(0.9, weights[key]));
  });

  return weights;
}

function calculateToneConfidence(topPerformers, toneChannelMapping) {
  const weights = {};

  // AUTO-AJUSTE: Refuerza tonos con alto engagement y penaliza los de bajo rendimiento
  if (topPerformers && topPerformers.length > 0) {
    topPerformers.forEach((performer, index) => {
      if (performer.tone) {
        // Más refuerzo para los primeros (mejores performers)
        const boost = 0.12 - (index * 0.015);
        weights[performer.tone] = (weights[performer.tone] || 0.5) + boost;
      }
    });
  }

  if (toneChannelMapping && Object.keys(toneChannelMapping).length > 0) {
    Object.entries(toneChannelMapping).forEach(([tone, channels]) => {
      if (Array.isArray(channels) && channels.length > 0) {
        // Más canales = más refuerzo
        const boost = 0.15 + (channels.length * 0.02);
        weights[tone] = (weights[tone] || 0.5) + boost;
      }
    });
  }

  // Normalizar todos los pesos
  Object.keys(weights).forEach(key => {
    weights[key] = Math.max(0.1, Math.min(0.9, weights[key]));
  });

  return weights;
}

function calculateTimingConfidence(bestTimes, bestDays) {
  const weights = { times: {}, days: {} };
  if (bestTimes && bestTimes.length > 0) {
    bestTimes.forEach(time => {
      weights.times[time] = 0.7;
    });
  }
  if (bestDays && bestDays.length > 0) {
    bestDays.forEach(day => {
      weights.days[day] = 0.7;
    });
  }
  return weights;
}

// AUTO-AJUSTE: Aplicar penalizaciones automáticas a patrones fallidos
// BLOQUEO TEMPORAL: Excluir patrones bloqueados
const avoidPatterns = failedPatterns
  .filter(pattern => {
    const patternStr = typeof pattern === 'object' ? pattern.pattern : pattern;
    return !blockedPatterns.includes(patternStr);
  })
  .map(pattern => {
    // Si el patrón tiene penalización negativa, se evita más
    if (typeof pattern === 'object' && pattern.penalty && pattern.penalty < 0) {
      return pattern.pattern || JSON.stringify(pattern);
    }
    return typeof pattern === 'string' ? pattern : (pattern.pattern || JSON.stringify(pattern));
  });

return {
  ...baseData,
  advancedMemory: advancedMemory,
  confidenceWeights: confidenceWeights,
  learnedBestChannels: advancedMemory.performanceMemory.bestPerformingChannels || [],
  avoidPatterns: avoidPatterns,
  preferredFormats: advancedMemory.preferenceMemory.preferredFormats || [],
  successfulPatterns: successfulPatterns,
  lastCognitiveVersion: lastCognitiveVersion
};

// Parsear la respuesta de OpenAI (API Completions - resource: text)
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const response = item.json || {};
    let analysis = {};
    let errorMessage = '';
    let rawContent = '';

    try {
      // API Completions devuelve: response.choices[0].text (no .message.content)
      rawContent = response.choices?.[0]?.text || 
                   response.choices?.[0]?.message?.content || 
                   response.text || 
                   response.message?.content || 
                   response.content || 
                   (typeof response === 'string' ? response : '') ||
                   '';

      if (!rawContent || rawContent.trim() === '') {
        errorMessage = 'OpenAI no devolvió contenido';
        throw new Error('Empty response from OpenAI');
      }

      let content = rawContent.trim();
      if (content.startsWith('```json')) {
        content = content.replace(/^```json\s*/g, '').replace(/\s*```$/g, '');
      } else if (content.startsWith('```')) {
        content = content.replace(/^```\s*/g, '').replace(/\s*```$/g, '');
      }
      content = content.trim();
      analysis = JSON.parse(content);
    } catch (error) {
      try {
        const jsonMatch = rawContent.match(/\{[\s\S]*\}/);
        if (jsonMatch && jsonMatch[0]) {
          analysis = JSON.parse(jsonMatch[0]);
        } else {
          errorMessage = 'No se pudo extraer JSON: ' + error.message;
          throw new Error('No JSON found');
        }
      } catch (parseError) {
        errorMessage = 'Error al parsear: ' + error.message;
        analysis = {
          objective: 'Error al analizar',
          tone: 'profesional',
          urgency: 'medium',
          contentType: 'post',
          targetAudience: '',
          keyMessages: [],
          hashtags: [],
          channels: []
        };
      }
    }

    // Obtener datos del nodo anterior de forma segura
    let normalizeMemoryData = {};
    let consolidateMemoryData = {};
    try {
      const normalizeNode = $('Normalize Memory');
      if (normalizeNode && normalizeNode.item) {
        normalizeMemoryData = normalizeNode.item.json || {};
      }
    } catch (e) {}

    try {
      const consolidateNode = $('Consolidate Advanced Memory');
      if (consolidateNode && consolidateNode.item) {
        consolidateMemoryData = consolidateNode.item.json || {};
      }
    } catch (e) {}

    const memory = normalizeMemoryData.memory || {};
    const result = {
      objective: analysis.objective || 'No especificado',
      tone: analysis.tone || memory.preferredTone || 'profesional',
      urgency: analysis.urgency || 'medium',
      contentType: analysis.contentType || 'post',
      targetAudience: analysis.targetAudience || '',
      keyMessages: Array.isArray(analysis.keyMessages) ? analysis.keyMessages : [],
      hashtags: Array.isArray(analysis.hashtags) ? analysis.hashtags : [],
      channels: Array.isArray(analysis.channels) ? analysis.channels : (normalizeMemoryData.channels || []),
      originalInstruction: normalizeMemoryData.instruction || consolidateMemoryData.instruction || '',
      analyzedAt: new Date().toISOString(),
      tenantId: normalizeMemoryData.tenantId || '',
      userId: normalizeMemoryData.userId || '',
      errorMessage: errorMessage || null
    };

    results.push({
      json: {
        ...normalizeMemoryData,
        ...consolidateMemoryData,
        analysis: result
      }
    });
  } catch (e) {
    results.push({
      json: {
        error: e.message,
        analysis: {
          objective: 'Error crítico',
          tone: 'profesional',
          urgency: 'medium',
          contentType: 'post',
          targetAudience: '',
          keyMessages: [],
          hashtags: [],
          channels: []
        }
      }
    });
  }
}

return results;

// Parsear la respuesta de OpenAI (API Completions - resource: text)
const response = $input.item.json;
let strategy = {};

try {
  // API Completions devuelve: response.choices[0].text
  let content = response.choices?.[0]?.text || response.choices?.[0]?.message?.content || response.text || response.message?.content || '';
  content = content.trim();
  if (content.startsWith('```json')) {
    content = content.replace(/```json\s*/g, '').replace(/```\s*/g, '');
  } else if (content.startsWith('```')) {
    content = content.replace(/```\s*/g, '');
  }
  strategy = JSON.parse(content);
} catch (error) {
  try {
    const rawContent = response.choices?.[0]?.text || response.choices?.[0]?.message?.content || response.text || response.message?.content || '';
    const jsonMatch = rawContent.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      strategy = JSON.parse(jsonMatch[0]);
    } else {
      const analysis = $('Parse Analysis').item.json.analysis || {};
      strategy = {
        mainMessage: analysis.objective || 'Mensaje principal de la campaña',
        cta: 'Descubre más',
        recommendedFormat: analysis.contentType || 'post',
        suggestedSchedule: { bestDays: ['lunes', 'miércoles', 'viernes'], bestTimes: ['09:00', '13:00', '18:00'], timezone: 'UTC' },
        contentStructure: { headline: analysis.objective || '', body: '', hashtags: analysis.hashtags || [], mentions: [] },
        channels: analysis.channels || [],
        tone: analysis.tone || 'profesional',
        targetAudience: analysis.targetAudience || '',
        keyPoints: analysis.keyMessages || []
      };
    }
  } catch (parseError) {
    const analysis = $('Parse Analysis').item.json.analysis || {};
    strategy = {
      mainMessage: 'Estrategia de marketing',
      cta: 'Descubre más',
      recommendedFormat: 'post',
      suggestedSchedule: { bestDays: ['lunes', 'miércoles', 'viernes'], bestTimes: ['09:00', '13:00', '18:00'], timezone: 'UTC' },
      contentStructure: { headline: '', body: '', hashtags: [], mentions: [] },
      channels: [],
      tone: 'profesional',
      targetAudience: '',
      keyPoints: []
    };
  }
}

const finalStrategy = {
  mainMessage: strategy.mainMessage || 'Mensaje principal de la campaña',
  cta: strategy.cta || 'Descubre más',
  recommendedFormat: strategy.recommendedFormat || 'post',
  suggestedSchedule: {
    bestDays: Array.isArray(strategy.suggestedSchedule?.bestDays) ? strategy.suggestedSchedule.bestDays : ['lunes', 'miércoles', 'viernes'],
    bestTimes: Array.isArray(strategy.suggestedSchedule?.bestTimes) ? strategy.suggestedSchedule.bestTimes : ['09:00', '13:00', '18:00'],
    timezone: strategy.suggestedSchedule?.timezone || 'UTC'
  },
  contentStructure: {
    headline: strategy.contentStructure?.headline || '',
    body: strategy.contentStructure?.body || '',
    hashtags: Array.isArray(strategy.contentStructure?.hashtags) ? strategy.contentStructure.hashtags : [],
    mentions: Array.isArray(strategy.contentStructure?.mentions) ? strategy.contentStructure.mentions : []
  },
  channels: Array.isArray(strategy.channels) ? strategy.channels : ($('Parse Analysis').item.json.channels || []),
  tone: strategy.tone || 'profesional',
  targetAudience: strategy.targetAudience || '',
  keyPoints: Array.isArray(strategy.keyPoints) ? strategy.keyPoints : []
};

const metadata = {
  tenantId: $('Parse Analysis').item.json.tenantId || '',
  generatedAt: new Date().toISOString(),
  basedOnAnalysis: $('Parse Analysis').item.json.analysis || {},
  basedOnMemory: $('Parse Analysis').item.json.memory || {}
};

return {
  ...$('Parse Analysis').item.json,
  strategy: { ...finalStrategy, metadata }
};

