let decisionRationale = [];
let learningSources = [];

// Factor 1: Canales con mejor performance (30%)
// AUTO-AJUSTE: Penaliza canales con bajo rendimiento histórico y refuerza los exitosos
const selectedChannels = Array.isArray(strategy.channels) ? strategy.channels : [];
const performanceMemory = advancedMemory.performanceMemory || {};
const channelKPIs = performanceMemory.channelKPIs || {};

// Ajustar confidenceWeights basado en resultados históricos
const adjustedChannelWeights = {};
selectedChannels.forEach(channel => {
  const baseWeight = confidenceWeights.channel?.[channel.toLowerCase()] || 0.5;
  const kpi = channelKPIs[channel.toLowerCase()] || {};
  const ctr = kpi.ctr || 0;
  const engagement = kpi.engagement || 0;

  // Ajuste automático: refuerza si tiene buen CTR/engagement, penaliza si no
  let adjustment = 0;
  if (ctr > 0.02) adjustment += 0.1; // CTR > 2% es bueno
  if (ctr < 0.005) adjustment -= 0.15; // CTR < 0.5% es malo
  if (engagement > 0.05) adjustment += 0.1; // Engagement > 5% es bueno
  if (engagement < 0.01) adjustment -= 0.15; // Engagement < 1% es malo

  adjustedChannelWeights[channel.toLowerCase()] = Math.max(0.1, Math.min(0.9, baseWeight + adjustment));
});

const channelConfidence = selectedChannels.reduce((acc, channel) => {
  const weight = adjustedChannelWeights[channel.toLowerCase()] || 0.5;
  return acc + weight;
}, 0) / Math.max(selectedChannels.length, 1);
confidenceScore += channelConfidence * 0.3;
