});

return publishJobs.map(job => ({ json: { ...$input.item.json, ...job } }));

// Procesar resultado de publicación (real o simulado)
const input = $input.item.json;
// Intentar obtener datos del item anterior usando el contexto de n8n
let publishData = {};
try {
  // Intentar obtener del nodo anterior (Prepare Publish Jobs)
  const previousNode = $('Prepare Publish Jobs');
  if (previousNode && previousNode.item) {
    publishData = previousNode.item.json;
  }
} catch (e) {
  // Si falla, usar datos del input actual
  publishData = input;
}

// Si el input tiene los datos directamente (viene de Check - Instagram/Facebook/TikTok cuando no coincide)
const channel = input.channel || publishData.channel || '';
const content = input.content || publishData.content || '';
const hashtags = input.hashtags || publishData.hashtags || '';
const mediaUrl = input.mediaUrl || publishData.mediaUrl || null;
const tenantId = input.tenantId || publishData.tenantId || '';
const campaignId = input.campaignId || publishData.campaignId || null;
const marketingPackId = input.marketingPackId || publishData.marketingPackId || null;
const generatedCopyId = input.generatedCopyId || publishData.generatedCopyId || null;

const isSimulated = !input.id && !input.post_id && !input.data && !input.success;
const hasError = input.error || input.error_code || (input.statusCode && input.statusCode >= 400);

if (hasError || isSimulated) {
  const simulatedResponse = {
    success: true,
    simulated: true,
    channel: channel.toLowerCase(),
    postId: `sim_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    publishedUrl: `https://${channel.toLowerCase()}.com/posts/sim_${Date.now()}`,
    publishedAt: new Date().toISOString(),
    message: `Publication simulated for ${channel} (no credentials configured)`,
    content: content,
    hashtags: hashtags,
    mediaUrl: mediaUrl,
    tenantId: tenantId,
    campaignId: campaignId,
    marketingPackId: marketingPackId,
    generatedCopyId: generatedCopyId
  };

  return simulatedResponse;
} else {
  const realResponse = {
    success: true,
    simulated: false,
    channel: channel.toLowerCase(),
    postId: input.id || input.post_id || input.data?.id || `real_${Date.now()}`,
    publishedUrl: input.permalink_url || input.link || input.data?.url || `https://${channel.toLowerCase()}.com/posts/${input.id || input.post_id}`,
    publishedAt: new Date().toISOString(),
    message: `Successfully published to ${channel}`,
    content: content,
    hashtags: hashtags,
    mediaUrl: mediaUrl,
    tenantId: tenantId,
    campaignId: campaignId,
    marketingPackId: marketingPackId,
    generatedCopyId: generatedCopyId
  };

  return realResponse;
}

// Consolidar todos los resultados de publicación
const allResults = $input.all();
const firstResult = allResults[0] || {};

// Obtener todos los publishing jobs guardados (vienen del HTTP Request - Save Publishing Job)
const publishingJobs = allResults
  .filter(r => r.json.id && (r.json.channel || r.json.tenantId))
  .map(r => ({
    id: r.json.id,
    channel: r.json.channel || 'unknown',
    status: r.json.status || 'Success',
    publishedUrl: r.json.publishedUrl || null
  }));

// Si no hay jobs, intentar obtener datos del primer resultado
const tenantId = firstResult.json.tenantId || '';
const campaignId = firstResult.json.campaignId || null;
const marketingPackId = firstResult.json.marketingPackId || null;

return {
  tenantId: tenantId,
  campaignId: campaignId,
  marketingPackId: marketingPackId,
  publishingJobIds: publishingJobs.map(j => j.id).filter(id => id),
  publishingJobs: publishingJobs,
  channels: publishingJobs.map(j => j.channel).filter(c => c),
  allPublished: publishingJobs.length > 0 && publishingJobs.every(j => j.status === 'Success'),
  success: true
};

// Consolidar resultados finales
const results = $input.all();
const consolidateData = $('Consolidate Publish Results').item.json;

const metricsResult = results.find(r => r.json.id && r.json.campaignId) || null;
const jobMetricsResult = results.find(r => r.json.publishingJobId) || null;

return {
  tenantId: consolidateData.tenantId,
  campaignId: consolidateData.campaignId,
  marketingPackId: consolidateData.marketingPackId,
  publishingJobIds: consolidateData.publishingJobIds,
  publishingJobs: consolidateData.publishingJobs,
  channels: consolidateData.channels,
  metricsSaved: metricsResult !== null,
  jobMetricsSaved: jobMetricsResult !== null,
  metricsId: metricsResult?.json.id || null,
  jobMetricsId: jobMetricsResult?.json.id || null,
  success: true,
  message: 'Complete marketing flow executed successfully'
};
